import React, { Component } from 'react';
import Person from './components/person';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      person: {
        name: 'John Doe',
        number: '123-456-7890',
        dateOfBirth: 'January 1, 2000',
        // Add any other properties you want to include
      },
    };
  }

  render() {
    return (
      <div>
        <Person person={this.state.person} />
      </div>
    );
  }
}

export default App;
