import React, { useState } from "react";

const ColorChanger = (props) => {
  // Change the color

  const [currentColor, setCurrentColor] = useState(0);
  const changeColor = () => {
    setCurrentColor((prevColor) => (prevColor + 1) % props.colors.length);
  };

  return (
    <div>
      <button onClick={changeColor}> CHANGE COLOR HERE</button>
      <div
        style={{
          backgroundColor: props.colors[currentColor],
          width: "200px",
          height: "200px",
          margin: "auto",
          padding: "auto",
        }}
      ></div>
    </div>
  );
};

const App = () => {
  const colors = ["red", "green", "blue", "yellow"];
  return (
    <div>
      <h1> Color changer app! </h1>

      <ColorChanger colors={colors} />
    </div>
  );
};

export default App;
